var mongoose = require('mongoose'),
    Schema   = mongoose.Schema;

var tropasModelo = new Schema({
  apellidoM: { type: String, trim: true, required: true},
  apellidoP: { type: String, trim: true, required: false},
  nombre: {type: String, trim: true, required: true},
  nivel: {type: Number, trim: true, required: true},
  appMovil: {type: Boolean, trim: true, required: true},
});

module.exports = mongoose.model('tropas', tropasModelo);
